import React, {useState} from 'react';
import AddSetDescription from './AddSetDescription';
import AddSetName from './AddSetName';
import AddWordPart1 from './AddWordPart1';
import AddWordPart2 from './AddWordPart2';
import AddWordBtn from './AddWordBtn';
import '../../style/CreateNewSet.css';

class Word {
    constructor(part1, part2) {
        this.id = Date.now();
        this.part1 = part1;
        this.part2 = part2;
        this.isLearnt = false;
        this.lastSeen = "01.01.2000"; // dd.mm.yyyy
    }
}
class WordSet {
    constructor(wordArray) {
        this.words = wordArray;
    }

    deleteWord(id) {
        this.words.filter(elem => elem.id !== id)
    }

    addWord(word) {
        this.words.push(word);
    }
}

function CreateNewSet() {

    // data initialization
    let wordSet = new WordSet([
        new Word('cat', 'koshka'), 
        new Word('dog', 'cobaka'), 
    ]);

    const [state, setState] = useState(wordSet);
    const addItem = (part1, part2) => {
        setState(() => {
            state.addItem(part1, part2);
            return state
        })
    }; 

    const deleteItem = (id) => {
        setState(() => {
            state.deleteItem(id);
            return state;
        });
