            {/* <h2>state.words[0].part1</h2> */}
            {/* <div className='todo_list'>
            {state.words.map(elem => <ToDoItem key = {elem.id} item={elem}/>)}
            </div> */}
        </div>
    )
}

export default CreateNewSet;
