import React, {useState} from 'react';
import '../../style/SetInfo.css';

const CircleDiagram = ({ data, width, height }) => {
	var offsets = [];
	data.reduce((acc, cur, index, arr) => {
		acc += !!data[index - 1] && data[index - 1].value;
		offsets.push(acc);
		return acc;
	}, 0);

	var items = data.map((item, index) => {
		return (
			<circle
				className="diagram__item"
				key={"key-" + item.color}
				r="16"
				cx="16"
				cy="16"
				stroke={item.color}
				strokeDasharray={`${item.value}, 100`}
				strokeDashoffset={-1 * offsets[index]}
			/>
		);
	});

	return (
		<svg
			className="diagram"
			viewBox="0 0 32 32"
			style={{ background: "#eee" }}
			width={width}
			height={height}
		>
			{items}
		</svg>
	);
};

function Diagram() {
	var settings = {
		width: 100,
		height: 100,
		data: [
			{
				color: "#39f",
				value: 20.4
			},
			{
				color: "#fc0",
				value: 38.7
			},
			{
				color: "#ff7500",
				value: 12.1
			}
		]
	};
	return (
		<div>
			<CircleDiagram
				width="200"
				height="200"
				data={[
					{
						color: "#39f",
						value: 20.4
					},
					{
						color: "#fc0",
						value: 38.7
					},
					{
						color: "#ff7500",
						value: 12.1
					}
				]}
			/>
			<CircleDiagram
				width="100"
				height="100"
				data={[
					{
						color: "yellow",
						value: 10
					},
					{
						color: "orange",
						value: 50.2
					},
					{
						color: "blue",
						value: 24.15
					}
				]}
			/>
		</div>
	);
};

export default Diagram;
