import React, {useState} from 'react';

import '../../style/LearnSet.css';


function FlashCard() {

    let wordSet = [
        {
            id: 1,
            part1: 'cat',
            part2: 'koshka'
        },
        {
            id: 2,
            part1: 'bog',
            part2: 'cobaka'
        }
    ]

    const [state, setState] = useState({
        text1: wordSet[0].part1,
        text2: wordSet[0].part2,
        wordId: 0
    })

    const onClickButton_FlipCard = () => {
        setState({
            text1: state.text2,
            text2: state.text1,
            wordId: state.wordId
        });
    }
    const onClickButton_NextCard = () => {
        const id = (state.wordId + 1 == length(wordSet) ? 0 : state.wordId + 1);
        setState({
            text1: wordSet[id].text1,
            text2: wordSet[id].text2,
            wordId: id
        });
    }

    return (
        <div>
            <button onClick={onClickButton_FlipCard} className='button' >Flip the card</button>
            <h1>{state.text1}</h1>
            <button onClick={onClickButton_NextCard} className='button' >Next word</button>
        </div>
    )
}

export default FlashCard;
