import React, {useState} from 'react';
import '../../style/LearnSet.css';
import FlashCard from './FlashCard'

let wordSet = [
    {
        id: 1,
        part1: 'cat',
        part2: 'koshka'
    },
    {
        id: 2,
        part1: 'bog',
        part2: 'cobaka'
    }
]

let wordId = 0;
let wordPart = 1;

const [state, setState] = useState({text: wordSet[wordId]})

const onClickButton = () => {
    setState({
        text: (wordPart == 1 ? wordSet[wordSet].part1 : wordSet[wordSet].part2)
    });
}


function LearnSet() {
    return (
        <div>
            <h2>Happy learning!</h2>

            {/* <FlashCard /> */}

            <div className='button-wrapper'>
                <button className='button' >Study again</button>
                <button onClick={onClickButton} className='button' >Flip the card</button>
                <button className='button' >Got it</button>
            </div>
        </div>
    )
}

export default LearnSet;
