import React, {useState} from 'react';
import {Fragment} from 'react';
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom'

// import ToDoList from './ToDoList'
import Home from './Home'
import LogIn from './LogIn'
import SignUp from './SignUp'
import NotFound from './NotFound';
import CreateNewSet from './CreateNewSet';

import {Context} from '../context'


// import WordSet from './WordSet';
// import Word from './Word.js'

const Word = require('./Word.js');
const WordSet = require('./WordSet');

// import About from './About'
// import NotFound from './NotFound';


function App() {

    let wordSet = new WordSet([
        new Word('cat', 'koshka'), 
        new Word('dog', 'sobaka'), 
    ]);

    const [state, setState] = useState(wordSet);
    const addItem = (part1, part2) => {
        setState(() => {
            state.addItem(part1, part2);
        return state
        })
    }; 

    const deleteItem = (id) => {
        setState(() => {
            state.deleteItem(id);
            return state;
            }
        );
    }; 

    return (
        <Context.Provider value={{deleteItem, addItem}}>
        <div>
            <Router>
                <Fragment>
                    <Routes>
                        <Route exact path="/" element={<Home />}/>
                        <Route exact path="/login" element={<LogIn />} />
                        <Route exact path="/signup" element={<SignUp />} />
                        <Route exact path="/create-new-set" element={<CreateNewSet state={state}/>} />
                        <Route path = '*' element={<NotFound />}/>
                        {/* <Route exact path="/about" element={<About />}/> */}
                        {/* <Route path = '*' element={<NotFound />}/> */}
                    </Routes>
                </Fragment>
            </Router>
        </div>
        </Context.Provider>
    )
}

export default App;
