            part2: 'мыш',
            isLearnt: true, 
            lastSeen: "04.01.2022"
        }
      ])

      const addItem = (id, title, done_flg) => {
        setState(() => [
          ...state,
          {id, title, done_flg}
        ])
      }; 

      const deleteItem = (id) => {
        setState(() => 
          state.filter(elem => elem.id !== id)
        );
      }; 

    return (
        <div>
            <Router>
                <Fragment>
                    <Routes>
                        <Route exact path="/" element={<Home />}/>
                        <Route exact path="/login" element={<LogIn />} />
                        <Route exact path="/signup" element={<SignUp />} />
                        <Route exact path="/create-new-set" element={<CreateNewSet />} />
                        <Route path = '*' element={<NotFound />}/>
                        {/* <Route exact path="/about" element={<About />}/> */}
                        {/* <Route path = '*' element={<NotFound />}/> */}
                    </Routes>
                </Fragment>
            </Router>
        </div>
    )
}

export default App;
