import React, {useState} from 'react';

import '../../style/LearnSet.css';


function FlashCard() {

    let wordSet = [
        {
            id: 1,
            part1: 'cat',
            part2: 'koshka'
        },
        {
            id: 2,
            part1: 'bog',
            part2: 'cobaka'
        }
    ]

    let wordId = 0;
    let wordPart = 1;

    const [state, setState] = useState({text: wordSet[wordId].part1})

    const onClickButton = () => {
        setState({
            text: (wordPart == 1 ? wordSet[wordId].part1 : wordSet[wordId].part2)
        });
    }

    return (
        <div>
            <button onClick={onClickButton} className='button' >Flip the card</button>
            <h1>{state.text}</h1>
        </div>
    )
}

export default FlashCard;
