import React, {useState} from 'react';

import '../../style/LearnSet.css';

import {Context} from '../../context'

function FlashCard({setId}) {

    // console.log(state, setId);

    var wordSet ; //Context.state.filter(elem => elem.id == setId)

    if (true) {
        wordSet = [
            {
                id: 1,
                part1: 'cat',
                part2: 'koshka'
            },
            {
                id: 2,
                part1: 'bog',
                part2: 'cobaka'
            }
        ]
    }

    const [state, setState] = useState({
        text1: wordSet[0].part1,
        text2: wordSet[0].part2,
        wordId: 0,
        wordSet: wordSet
    })

    const onClickButton_FlipCard = () => {
        setState({
            text1: state.text2,
            text2: state.text1,
            wordId: state.wordId,
            wordSet: state.wordSet
        });
    }
    const onClickButton_NextCard = () => {
        const id = (state.wordId + 1 == (wordSet.length) ? 0 : state.wordId + 1);

        setState(() => {
            console.log(id);
            console.log(state.wordSet[id].text1);
            return  {
                text1: state.wordSet[id].part1,
                text2: state.wordSet[id].part2,
                wordId: id,
                wordSet: wordSet
            }
        });
        console.log(state.text1, state.text2, state.log)
    }

    return (
        <div>
            <div className='card-wrapper'>
                <div className='card'>
                    <h2>{state.text1}</h2>
                </div>
            </div>

                <div className='button-wrapper'>
                    <button className='button study_again' onClick={onClickButton_NextCard} >Study again</button>
                    <button onClick={onClickButton_FlipCard} className='button' >Flip the card</button>
                    <button className='button got_it' onClick={onClickButton_NextCard} >Got it</button>
                </div>
            </div>

    )
}

export default FlashCard;
