import React, {useState} from 'react';

import '../../style/LearnSet.css';


function FlashCard() {

    let wordSet = [
        {
            id: 1,
            part1: 'cat',
            part2: 'koshka'
        },
        {
            id: 2,
            part1: 'bog',
            part2: 'cobaka'
        }
    ]

    const [state, setState] = useState({
        text1: wordSet[0].part1,
        text2: wordSet[0].part2,
        wordId: 0
    })

    const onClickButton = () => {
        console.log({wordPart});
        // wordPart = 3 - wordPart;
        // console.log({wordPart});
        setState(() => {
            // text: (wordPart === 1 ? wordSet[wordId].part1 : wordSet[wordId].part2)
            wordPart = 3 - wordPart;
            let new_text = "";
            if (wordPart == 1) {
                console.log("If", {wordPart});
                new_text = wordSet[wordId].part1;
                console.log(new_text);
            }
            else {
                console.log("Else", {wordPart});
                new_text = wordSet[wordId].part2;
                console.log(new_text);
            }
            return {text: new_text};
        });
    }

    return (
        <div>
            <button onClick={onClickButton} className='button' >Flip the card</button>
            <h1>{state.text}</h1>
        </div>
    )
}

export default FlashCard;
